import React, { Component } from 'react';
import '../App.css';

class TodoList extends Component {
  render() {
    const tasks = this.props.tasks;

    return (
      <div className="tasks">
        <form>
        {tasks.map((item, index) =>
            (<div className="task">
               <label htmlFor = {index}>
                <input type = 'checkbox' id = {index}  checked = {item.done} onChange = {this.props.onClick}/>
                {item.content}
                </label>
                <div>
                  <button className = "task-button" onClick = {this.props.editTask} id = {`e${index}`}>
                      <img src="./../img/edit.png" alt=""/>
                  </button>
                  <button className = "task-button" onClick = {this.props.deleteTask} id = {`d${index}`}>
                      <img src="./../img/trash.png" alt=""/>
                  </button>
                </div>
            </div>) )
        }
        </form>
      </div>
    );
  }
}

export default TodoList;
