import React, { Component } from 'react';
import '../App.css';

class TodoList extends Component {
  render() {
    const tasks = this.props.tasks;
    const taskSorting=(a, b) => {
        if (a.index > b.index) return -1;
        if (a.index < b.index) return 1;
         }
    tasks.sort(taskSorting);
    console.log( "tasks", tasks)
    return (
      <div className="tasks">
        <form>
        {tasks.map((item, index) =>
            (<div className="task" key = {item.index}>
               <label className = "task-label" htmlFor = {index}>
                <input className = "task-check" type = 'checkbox' id = {item.index}  checked = {item.done} onChange = {this.props.onClick}/>
                {item.content}
                </label>
                <div>
                  <button className = "task-button task-button_edit" onClick = {this.props.editTask} id = {`e${index}`}>
                  </button>
                  <button className = "task-button task-button_delete" onClick = {this.props.deleteTask} id = {`d${index}`}>
                  </button>
                </div>
            </div>) )
        }
        </form>
      </div>
    );
  }
}

export default TodoList;
